  --mdui-elevation-level1: 0 0.5px 1.5px 0 rgba(0, 0, 0, 0.19);
}

/* 读取 level1 级别的高度对应的阴影值 */
.element {
  box-shadow: var(--mdui-elevation-level1);
}
